settings.customcssrules:
html[dark], [dark] {
  --yt-spec-base-background: #000C1D !important; /* background */
  --yt-spec-raised-background: #001122 !important; /* header playlist */
  --ytd-searchbox-background: #001122 !important; /* background search */
  --efyt-control-bar-background-color: #102a44 !important; /* background menu efyt */
  --yt-spec-menu-background: #102a44 !important; /* background menu */
}

#logo-icon {
  content: url("https://raw.githubusercontent.com/horionclient/Horion/master/.github/horion-banner.png") !important;
  width: 85px;
  height: 40px;
  object-fit: contain;
}

.ytp-play-button .ytp-svg-fill {
  fill: red !important;
}

#country-code {
  visibility: hidden;
}

#country-code::before {
  content: "M!nee";
  visibility: visible;
}

/* ********** Expand full ********** */ 

body.efyt-wide-player ytd-watch-flexy[theater] #movie_player:not(.ytp-fullscreen) video.html5-main-video, body.efyt-wide-player ytd-watch-flexy[theater] #movie_player:not(.ytp-fullscreen) .ytp-iv-video-content {
  width: 100vw !important ;
  object-fit: contain;
  left: 0 !important;
}

body.efyt-wide-player ytd-watch-flexy[theater]:not([fullscreen]) #player-theater-container, body.efyt-wide-player ytd-watch-flexy[theater]:not([fullscreen]) #player-wide-container, body.efyt-wide-player ytd-watch-flexy[full-bleed-player]:not([fullscreen]) #full-bleed-container.ytd-watch-flexy, body.efyt-wide-player ytd-watch-flexy[full-bleed-player]:not([fullscreen]) #player-full-bleed-container.ytd-watch-flexy, body.efyt-wide-player ytd-watch-flexy[theater] #movie_player:not(.ytp-fullscreen) video.html5-main-video, body.efyt-wide-player ytd-watch-flexy[theater] #movie_player:not(.ytp-fullscreen) .ytp-iv-video-content { 
  height: 100vh !important ;
}

#full-bleed-container {
  margin-top: 12px !important;
}

/* ********** hidden Search bar ********** */

ytd-watch[theater] #player.ytd-watch {
  padding-top: 15px !important;
}

#masthead-container {
  z-index: 1000;
  top: 0 !important;
  opacity: 0;
  left: 0;
  right: 0;
  width: 100% !important;
  position: relative;
  margin-right: -50%;
  transform: translate(-50%, 0%);
  transition: all 400ms ease !important;
}

#masthead-container:hover {
  top: 0 !important;
  right: 0;
  left: 0; 
  opacity: .9;
  width: 100% !important;
}

ytd-page-manager {
  margin-top: -12px !important;
}

#chips-wrapper.ytd-feed-filter-chip-bar-renderer {
  /* position: fixed !important; */
  top: 0 !important;
  padding-top: 56px !important; 
}

#chips-content {
  margin-top: 10px;
}

#contents.ytd-section-list-renderer > .ytd-section-list-renderer:not(:last-child):not(ytd-page-introduction-renderer):not([item-dismissed]).ytd-section-list-renderer {
  border-color: transparent;
} 

#header.style-scope .ytd-search {
  margin-top: 60px;
}

/* ********** for Video cc translator extention ********** */
.sc-dPyBCJ  {
  margin-top: 0 !important;
}

#contents.ytd-rich-grid-renderer {
  margin-top: 62px !important;
}

